image_optimisation.npm_script:
"scripts": {
  "images:optimise": "squoosh-cli --avif '{\"quality\":50}' --webp '{\"quality\":80}' -d ./landing/assets ./landing/assets/src/*.png",
  "images:resize": "for size in 640 960 1280; do convert ./landing/assets/src/hero-black-box.png -resize ${size}x ./landing/assets/hero-black-box-${size}.webp; done",
  "build:images": "npm run images:optimise && npm run images:resize"
}

image_optimisation.cdn_headers:
# Vercel vercel.json headers for images
{
  "headers": [
    {
      "source": "/landing/assets/(.*)",
      "headers": [
        { "key": "Cache-Control", "value": "public, max-age=31536000, immutable" },
        { "key": "Vary", "value": "Accept" }
      ]
    }
  ]
}
